import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { getProjectById } from '../data/projects';

const ProjectPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const project = id ? getProjectById(id) : undefined;

  if (!project) {
    return (
      <div className="main">
        <h1>Project not found</h1>
        <p>
          <Link to="/work" className="link-text">
            &lt;- Back to work
          </Link>
        </p>
      </div>
    );
  }

  return (
    <>
      <div className="btn">
        <h3 className="btn-text">
          <Link to="/" className="text">
            back to home
          </Link>
        </h3>
      </div>

      <div className="main">
        <h1>{project.title}</h1>
        {project.subtitle && <p>{project.subtitle}</p>}

        <p className="project-summary">
          <b>Problem: </b>{project.problem}
          <br /><br />
          <b>Method: </b>{project.method}
          <br /><br />
          <b>Solution: </b>{project.solution}
          <br /><br />
          <b>Outcome: </b>{project.outcome}
        </p>

        {project.externalLink && project.externalLinkText && (
          <h3>
            <a className="text" href={project.externalLink} target="_blank" rel="noopener noreferrer">
              {project.externalLinkText}
            </a>
          </h3>
        )}

        <img 
          className="project-banner" 
          src={project.image} 
          alt={project.imageAlt} 
        />

        {project.content && (
          <h3>{project.content}</h3>
        )}
      </div>
    </>
  );
};

export default ProjectPage;
